import React, { Component } from 'react'

export default class AddTodo extends Component {

    onSubmit(e) {
		e.preventDefault();

		let date = GetFormattedDate(this.state.date)

		 this.setState({ isLoading: true });


		for (var i = 0; i < 5; i++) {
		  let name = this.state.csv[i].["name.first"]
		  let city = this.state.csv[i].["location.city"]
		  let number = this.state.csv[i].phone		

		  axios.get(`http://api.weatherapi.com/v1/forecast.json?key=ad05d6c949974ef789c25002202310&q=${this.state.csv[i].["location.city"]}&days=10`)
	  	  .then(res => {
	  	  	let _date = res.data.forecast.forecastday

	  	  	for (var i = 0; i < _date.length; i++) {

	  	  	if(_date[i].date === date){
	  	  		let max = res.data.forecast.forecastday[0].day.maxtemp_c
		  		let min = res.data.forecast.forecastday[0].day.mintemp_c
		  		let avg = res.data.forecast.forecastday[0].day.avgtemp_c
		  		let condition = res.data.forecast.forecastday[0].day.condition.text


	  		let data = {
	  			name,
	  			city,
	  			number,
	  			max,
	  			min,
	  			avg,
	  			condition
	  		}


 			axios.post('http://localhost:5000/client/add', data)
            .then(res => {

            	if(res.data === 'Client Added!') {
            		this.setState({ isLoading: false, error: ''});
            		window.location = '/'
            	}
             })
            break;
	  	  } else {
	  	  			this.setState({
		  	  		isLoading: false,
		  	  		error: `Can't fetch weather from that date! Please change date from ${GetFormattedDate(new Date())} until ${GetFormattedDateTomorrow(new Date())}`
	  	  	      })
	  	        }
	  	  	}

	  	 })

	  	  }

	}
    render() {
        return (
            <form onSubmit={this.onSubmit}>
                <h6>Enter your Todo Here: </h6>
                <div class="form-group">
                    <label for="exampleInputEmail1">Todo</label>
                    <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter task here" />
                </div>
                <button type="submit" class="btn btn-primary">Submit</button>
            </form>
        )
    }
}
