import React, { Component } from 'react'
import axios from 'axios'

export default class Home extends Component {
    constructor(props) {

        super(props);

        this.state = {
            isLoading: false,
            todos: [],
            open: false
        }

        this.onDeleteTodo = this.onDeleteTodo.bind(this)
    }

    componentDidMount() {
        axios.get('http://localhost:5000/todoApp/')
            .then(res => {
                this.setState({
                    todos: res.data,
                    isLoading: true
                })
            })
    }

    onDeleteTodo(e, id) {
        axios.delete(`http://localhost:5000/todoApp/delete/${id}`)
            .then(res => {
                this.setState({
                    todos: this.state.todos.filter(data => data._id !== id)
                })
            })
    }

    onEditTodo() {
        console.log('test')
        this.setState({ open: true })
    }

    render() {
        return (
            <>
                <table class="table table-hover">
                    <thead>
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">Todo</th>
                            <th scope="col">Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.todos.map((data, i) =>
                                <tr>
                                    <th scope="row">{i}</th>
                                    <td>{data.todo}</td>
                                    <td style={{ cursor: 'pointer' }}>
                                        <span type="button" class="btn btn-primary" data-toggle="modal" data-target="#exampleModal"><i class="fas fa-edit"></i></span>&nbsp;&nbsp;&nbsp;
                                    <span onClick={(e) => this.onDeleteTodo(e, data._id)}><i class="fas fa-trash"></i></span>
                                    </td>
                                </tr>
                            )
                        }
                    </tbody>
                </table>
                {
                    this.state.open && 
                    <div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                    <div class="modal-dialog" role="document">
                      <div class="modal-content">
                        <div class="modal-header">
                          <h5 class="modal-title" id="exampleModalLabel">Modal title</h5>
                          <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                            <span aria-hidden="true">&times;</span>
                          </button>
                        </div>
                        <div class="modal-body">
                          ...
                        </div>
                        <div class="modal-footer">
                          <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
                          <button type="button" class="btn btn-primary">Save changes</button>
                        </div>
                      </div>
                    </div>
                  </div>
                }
            </>
        )
    }
}
